Scripts.OnValueChanged:
Cryo.db.profile.CryolysisAngle = this:GetValue();
local x, y = CryolysisButton:GetCenter();
GameTooltip:SetText(this:GetValue());
Cryo:UpdateButtonsScale();

Scripts.OnEnter:
NBx, NBy = CryolysisButton:GetCenter();
NBx = NBx * (Cryo.db.profile.CryolysisButtonScale / 100);
NBy = NBy * (Cryo.db.profile.CryolysisButtonScale / 100);
GameTooltip:SetOwner(this, "ANCHOR_RIGHT");
GameTooltip:SetText(this:GetValue().." %");

Scripts.OnValueChanged:
if this:GetValue() ~= Cryo.db.profile.CryolysisButtonScale then
	CryolysisButton:ClearAllPoints();
	GameTooltip:SetText(this:GetValue().." %");
	Cryo.db.profile.CryolysisButtonScale = this:GetValue();
	CryolysisButton:SetPoint("CENTER", "UIParent", "BOTTOMLEFT", NBx / (Cryo.db.profile.CryolysisButtonScale / 100), NBy / (Cryo.db.profile.CryolysisButtonScale / 100));
	CryolysisButton:SetScale(Cryo.db.profile.CryolysisButtonScale / 100);
	Cryo:UpdateButtonsScale();
end

if this:GetValue() ~= Cryo.db.profile.CryolysisStoneScale then
	GameTooltip:SetText(this:GetValue().."%");
	Cryo.db.profile.CryolysisStoneScale = this:GetValue();
	CryolysisFoodButton:SetScale(Cryo.db.profile.CryolysisStoneScale / 100);
	CryolysisDrinkButton:SetScale(Cryo.db.profile.CryolysisStoneScale / 100);
	CryolysisManaStoneButton:SetScale(Cryo.db.profile.CryolysisStoneScale / 100);
	CryolysisLeftSpellButton:SetScale(Cryo.db.profile.CryolysisStoneScale / 100);
	CryolysisEvocationButton:SetScale(Cryo.db.profile.CryolysisStoneScale / 100);
	CryolysisRightSpellButton:SetScale(Cryo.db.profile.CryolysisStoneScale / 100);
	CryolysisBuffMenuButton:SetScale(Cryo.db.profile.CryolysisStoneScale / 100);
	CryolysisMountButton:SetScale(Cryo.db.profile.CryolysisStoneScale / 100);
	CryolysisPortalMenuButton:SetScale(Cryo.db.profile.CryolysisStoneScale / 100);
	Cryo:UpdateButtonsScale();
	Cryo:CreateMenu();
end

Scripts.OnClick:
if (this:GetChecked()) then
	Cryo.db.profile.CryolysisLockServ = true;
	Cryo:ClearAllPoints();
	Cryo:UpdateButtonsScale();
	Cryo:DragControl(false);
	if not Cryo.db.profile.NoDragAll then
		CryolysisButton:RegisterForDrag("LeftButton");
		CryolysisSpellTimerButton:RegisterForDrag("LeftButton");
	end
else
	Cryo.db.profile.CryolysisLockServ = false;
	Cryo:ClearAllPoints();
	CryolysisFoodButton:SetPoint("CENTER", "UIParent", "CENTER", -121,-100);
	CryolysisDrinkButton:SetPoint("CENTER", "UIParent", "CENTER", -87,-100);
	CryolysisManaStoneButton:SetPoint("CENTER", "UIParent", "CENTER", -53,-100);
	CryolysisLeftSpellButton:SetPoint("CENTER", "UIParent", "CENTER", -17,-100);
	CryolysisEvocationButton:SetPoint("CENTER", "UIParent", "CENTER", 17,-100);
	CryolysisRightSpellButton:SetPoint("CENTER", "UIParent", "CENTER", 53,-100);
	CryolysisBuffMenuButton:SetPoint("CENTER", "UIParent", "CENTER", 87,-100);
	CryolysisMountButton:SetPoint("CENTER", "UIParent", "CENTER", 121,-100);
	CryolysisPortalMenuButton:SetPoint("CENTER", "UIParent", "CENTER", 155,-100);
	Cryo:DragControl(true);
	Cryo.db.profile.NoDragAll = false;
	CryolysisLock_Button:SetChecked(false);
	CryolysisButton:RegisterForDrag("LeftButton");
	CryolysisSpellTimerButton:RegisterForDrag("LeftButton");
end

if (this:GetChecked()) then
	Cryo:DragControl(false);
	CryolysisButton:RegisterForDrag("");
	CryolysisSpellTimerButton:RegisterForDrag("");
	Cryo.db.profile.NoDragAll = true;
else
	if not Cryo.db.profile.CryolysisLockServ then
		Cryo:DragControl(true);
	end
	CryolysisButton:RegisterForDrag("LeftButton");
	CryolysisSpellTimerButton:RegisterForDrag("LeftButton");
	Cryo.db.profile.NoDragAll = false;
end

if (this:GetChecked()) then
	Cryo.db.profile.ManaStoneMenuPos = -34;
else
	Cryo.db.profile.ManaStoneMenuPos = 34;
end
Cryo:CreateMenu();

Scripts.OnValueChanged:
GameTooltip:SetText(this:GetValue());
if Cryo.db.profile.ManaStoneMenuAnchor ~= this:GetValue() then
	Cryo.db.profile.ManaStoneMenuAnchor = this:GetValue();
	Cryolysis_ChangeOfsy("ValueChange", "ManaStone");
end

Scripts.OnClick:
if (this:GetChecked()) then
	Cryo.db.profile.BuffMenuPos = -34;
else
	Cryo.db.profile.BuffMenuPos = 34;
end
Cryo:CreateMenu();

Scripts.OnValueChanged:
GameTooltip:SetText(this:GetValue());
if Cryo.db.profile.BuffMenuAnchor ~= this:GetValue() then
	Cryo.db.profile.BuffMenuAnchor = this:GetValue();
	Cryolysis_ChangeOfsy("ValueChange", "Buff");
end

Scripts.OnClick:
if (this:GetChecked()) then
	Cryo.db.profile.PortalMenuPos = -34;
else
	Cryo.db.profile.PortalMenuPos = 34;
end
Cryo:CreateMenu();

Scripts.OnValueChanged:
GameTooltip:SetText(this:GetValue());
if Cryo.db.profile.PortalMenuAnchor ~= this:GetValue() then
	Cryo.db.profile.PortalMenuAnchor = this:GetValue();
	Cryolysis_ChangeOfsy("ValueChange", "Portal");
end
